@import url('https://fonts.googleapis.com/css2?family=Martel+Sans:wght@200;300&display=swap');





/* Box sizing rules */
*,
*::before,
*::after {
  box-sizing: border-box;
}

/* Remove default margin */
body,
h1,
h2,
h3,
h4,
p,
figure,
blockquote,
dl,
dd {
  margin: 0;
}

/* Remove list styles on ul, ol elements with a list role, which suggests default styling will be removed */
ul[role='list'],
ol[role='list'] {
  list-style: none;
}

/* Set core root defaults */
html:focus-within {
  scroll-behavior: smooth;
}

/* Set core body defaults */
body {
  min-height: 100vh;
  text-rendering: optimizeSpeed;
  line-height: 1.5;
}

/* A elements that don't have a class get default styles */
a:not([class]) {
  text-decoration-skip-ink: auto;
}

/* Make images easier to work with */
img,
picture {
  max-width: 100%;
  display: block;
}

/* Inherit fonts for inputs and buttons */
input,
button,
textarea,
select {
  font: inherit;
}

/* Remove all animations, transitions and smooth scroll for people that prefer not to see them */
@media (prefers-reduced-motion: reduce) {
  html:focus-within {
   scroll-behavior: auto;
  }

  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }
}



:root {


    --bg-clr: #d9dad8;
    --accent-clr: #B4FFC9;
    --font-clr: #CDB4ff;

    --body-fnt: "Helvetica";
    --title-fnt: 'Martel Sans', sans-serif;
}



body {
    background-color: var(--bg-clr);
    width: auto;
    height: 100vh;


}


header {

    width: 100%;
    margin: 0;
    display: flex;
    position: fixed;
    background-color: var(--bg-clr);
    border: 2px solid black;
    z-index: 3;
    top: 0px;
    box-sizing: border-box;


}


/**Navigation Styles **/




nav {
    display: flex;
    flex-flow: row nowrap;
    height: 75px;
    width: 100%;
    justify-content: center;
    align-items: center;  




}

.logo {
    font-size: 2rem;
    height: 100%;
    padding: 0px;
    margin: 0px;
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 15px;
    font-family: var(--title-fnt);
    border-right: 2px solid black;



    @media screen and (max-width: 1200px){
      border: none;
      padding: 0px;
      padding-top: 10px;
      align-items: center;
    }


}



.nav-links {
    width: 100%;
    height: 100%;
    display: flex;
    flex-flow: row nowrap;
    justify-content: space-evenly;
    align-items: center;





    a {

        text-decoration: none;
        color: black;
        text-transform: uppercase;
        font-family: var(--body-fnt);
        border-right: 2px solid black;
        text-align: center;
        width: 100%;
        height: 100%;
        padding-top: 25px;  
        transition-property: background-color;
        transition: ease-in-out 500ms;


        &:hover {
          background-color: var(--accent-clr);
        }



    }


    @media screen and (max-width: 1200px) {
        display: none;
    }

}



.hamburger-btn {
    display: flex;
    background-color: var(--bg-clr);
    border: 2px solid black;
    width: 25px;
    height: 25px;
    margin: 5px; 
    margin-right: 15px;
    margin-left: 215px;

    align-self: center;
    justify-content: center;
    align-items: center;
    cursor: pointer;
    position: relative;
    transition: all 500ms;
    z-index: 5;




    @media screen and (min-width: 1200px) {
        display: none;


    }


}


.ham-top, .ham-middle, .ham-bottom {

    width: 15px;
    height: 2px;
    background-color: black;
    position: absolute;
    right: 3px;
    top: 4px;
    transform: rotate(0deg);


}

.open {
  transform: translateY(0px) rotate(90deg);



}

.open .ham-top {
  transform: translateY(5px) translate(5px) rotate(45deg);
}

.opem .ham-middle {
  display: none;

}


.open .ham-bottom {
  transform: translateY(5px) tralsate(5px);
  transform: rotate(-45deg);

}

.ham-middle {
    transform: translateY(5px);

}



.ham-bottom {
    transform: translateY(10px);
}


.mobile-menu {
    display: none;
    flex-flow: column nowrap;
    width: 50%;
    left: 50%;
    margin-top: 50px;
    height: 100vh;




}


.menu-links {
    display: flex;
    flex-flow: column nowrap;
    background-color: var(--accent-clr);
    height: 100vh;
    padding-top: 50%;
    justify-content: center;
    align-items: center;


    a {
        font-size: 2rem;
        text-decoration: none;
        font-family: var(--title-fnt);
        border: 2px solid black;
        margin: 10px;
        text-align: center;
        width: 65%;
        text-transform: uppercase;
        color: black;

    }


}


.flex {
    display: flex;
}

.opacity {
    opacity: 1;
}

.selected {
  background-color: var(--font-clr);
}



/* Main Container styles */


.container__main {

  display: flex;
  flex-flow: column nowrap;
  width: 100%;

  border: 2px solid black;
  padding-bottom: 100vh;
}




/* hero styles */



.hero {

  width: 100%;
  display: flex;
  flex-flow: row nowrap;
  height: 100vh;

  @media screen and (max-width: 1200px) {


  }

}


.hero_main {
  display: flex;
  flex-flow: row nowrap;
  width: 100%;



  @media screen and (max-width: 1200px) {
    flex-flow: column nowrap;
  }


  .hero-info {
    width: 50%;
    height: 100vh;

    display: flex;
    flex-flow: column nowrap;


    @media screen and (max-width: 1200px) {
      width: 100%;
    }



    .hero-info-content {
      padding-left: 15%;
      padding-right: 15%;
      padding-top: 35%;


      h1 {
        font-size: 7rem;

      }

      h2 {
        font-size: 2.5rem;
      }

    }


    /*Hero Btn styles */

    .hero-btn {
      display:flex;
      margin-top: 40%;
      background-color: var(--font-clr);
      border: none; 
      height: 100px;
      font-size: 3rem;
      font-family: var(--body-fnt);
      text-transform: uppercase;
      cursor: pointer;
      box-sizing: border-box;
      border-top: 2px solid black;
      border-bottom: 2px solid black;
      align-items:center;
      justify-content: center;
      text-align: center;
      transition: all 300ms ease-in-out;

      text-decoration: none;
      color: black;


      #hero-btn-icon {

        background-color: var(--font-clr);
        transition: all 700ms ease-in-out;
        transform: rotate(0deg);
        margin-left: 15px;
        margin-bottom: 3px;
        padding: 0px;
      }

      &:hover  {

        border: 5px solid black;


        #hero-btn-icon{
          background-color: transparent;

        transform: rotate(360deg);
        }



      }

      @media screen and (min-width: 1440px){
        margin-top: 36%;

      }

      @media screen and (min-width: 1200px){
        font-size: 1.5rem;

      }


    }


  }


  /* Hero img box */


  .hero-img {
    background-image: url(../images/profile.png);
    width: 50%;
    height: 100%;
    background-repeat: no-repeat;
    background-position: center;
    background-size: cover;
    border: 2px solid black;


    @media screen and (max-width: 1200px) {
      width: 100%;

    }
  }


}



/* about section */



.about {
  width: 100%;
  height: 100vh;



}

.about_box {
  width: 65%;

  margin: 0 auto;
  margin-top: 200px;

  border: 2px solid black;



  h3 {
    font-size: 4rem;
    padding-bottom: 50px;
    font-family: var(--body-fnt);
    padding: 15px;
  }
}


.about_box-items {
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-template-rows: 1fr 1fr;

  height: 100%;
  width: 100%;
  margin: 0 auto;


  .about_box-item {

    border-top: 2px solid black;




      .item-title {
        font-size: 2rem;
        padding: 15px;
        font-family: var(--body-fnt);
      }

      p {
        font-size: 1.2rem;
        padding-left: 100px;
        padding-right: 100px;
        padding-bottom: 30px;
        font-family: var(--body-fnt);
      }
  }
}

.about_box-item:nth-child(2) {
  border-left: 2px solid black;

}

.about_box-item:nth-child(4) {
  border-left: 2px solid black;

}


.about_box-buttons {
  display: flex;
  flex-flow: column nowrap;
  height: 200px;
  width: 100%;
  border-top: 2px solid black;
  overflow: hidden;



}



.box_button-title{

  display:flex;
  flex-flow: row nowrap;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 100%;
  visibility: visible;
  opacity: 1;
  transition: all 500ms linear;
 z-index: 10;
  position: relative;
  background-color: var(--accent-clr);



    strong {
      font-size: 3rem;
  font-family: var(--title-fnt);
    }

}
.box_buttons {
  display: flex;
  visibility: hidden;
  position: absolute;
  flex-flow: row nowrap;
  margin-left: 25px;
  transition: all 500ms ease-in;
  width: 80%;
  opacity: 0;

  transform: translateY(-200px);

}

#box_button {
  display: flex;

  width: 150px;
  height: 50px;
  border-radius: 100vw;

  justify-content: center;
  text-align: center;
  align-items: center;
  border: 2px solid black;
  text-decoration: none;
  color: black;
  background-color: var(--accent-clr);

  font-family: var(--body-fnt);
  transition: all 300ms linear;

  &:hover {
    background-color: #CDB4ff;
    box-shadow: 7px 4px 0px 2px black;
    transform: translateY(5px) translateX(5px);
  }

}


.about_box-buttons:hover .box_buttons {
  visibility: visible;
  transform: translateY(20px) ;
  opacity: 1;
}

.about_box-buttons:hover .box_button-title {
  visibility: hidden;
  opacity: 0;
  transform: translateY(-200px);


}




.box_button1, .box_button4, .box_button9 {  
  z-index: 2;
  margin-top: 40px;
  margin-right: 20px;


}

.box_button3, .box_button7 {  
  z-index: 4;
  margin-top: 25px;
  margin-right: 30px;


}

.box_button2, .box_button6 {  
  z-index: 4;
  margin-top: 60px;
  margin-right: 50px;


}

.box_button8 {  
  z-index: 4;
  margin-top: 75px;
  margin-right: 50px;


}
